disp("Hello, World!");# vectors and plotx = [0 1 2 3 4 5 6];y = [3 4 5 1 4 6 7];plot (x, y);# matrix and vectorM = [1 3 5; 4 5 6];V = [3; 4; 5];r = M * V# matrix transposeMT = M' # matrix navigation, selection# useful tutorial: # https://en.wikibooks.org/wiki/Octave_Programming_Tutorial/Vectors_and_matricesA (1, 3)  # A (m, n)A (2, :)  # A (m, the whole row)A(:, end) # A last columneye(9)ones(9)gallery("chow", 7) # famous matrix gallery!gallery("circul", 7)gallery("neumann", 9)#ranges1:3:10    # start, step, end0:10:100# nice graphx = -10:0.1:10; # Create an evenly-spaced vector from -10..10y = sin (x);    # y is also a vectorplot (x, y);title ("Simple 2-D Plot");xlabel ("x");ylabel ("sin (x)");